        formData.append('userAgent', navigator.userAgent); // 1. Ø¥Ø±Ø³Ø§Ù„ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„Ù…ØªØµÙØ­
        const fbp = getCookie('_fbp'); // 2. Ù‚Ø±Ø§Ø¡Ø© ÙƒÙˆÙƒÙŠ Ø§Ù„Ù…ØªØµÙØ­
