    buttonContainer.appendChild(addSubSectionButton);
    buttonContainer.appendChild(removeSectionButton);
    // --- â–²â–²â–² Ù†Ù‡Ø§ÙŠØ© Ø§Ù„Ø¬Ø²Ø¡ Ø§Ù„Ø¬Ø¯ÙŠØ¯ â–²â–²â–² ---

    sectionWrapper.appendChild(titleInput);
